import { createContext, useContext, useState } from "react";

export const LibraryContext=createContext()
const themeColorWhite={
   color:"black",
   backgroundColor:"white"

}
const themeColorBlack={
   color:"white",
   backgroundColor:"black"

}
export function LibraryProvider({children}){
   const[theme,setTheme]=useState('black')
   const[themeColor,setThemeColor]=useState(themeColorBlack);
   function colorchanger(color){
      if(theme==='light'){
         setThemeColor(themeColorWhite)
      }else{
         setThemeColor(themeColorBlack)
      }
   }

   return( <LibraryContext.Provider value={themeColor,setTheme}>
        {children}
    </LibraryContext.Provider>
   )
}
export function useLibraryContext(){
   return useContext(LibraryContext)
}
